import React, { useState, useEffect } from 'react';
import Axios from "axios"


const Capacity = () => {

	const [capacity, setCapacity] = useState(100)
	const [maxCapacity, setMaxCapacity] = useState()
	const [minCapacity, setMinCapacity] = useState()
	const [selectedItemId, setSelectedItemId] = useState(selec);
	const [capacityData, setCapacityData] = useState([]);


	const getInfoCapacity = async () => {
		try {
			const response = await Axios.get("http://localhost:3001/capacity");
			setCapacityData(response.data);
			const capacityValues = response.data.map((item) => item.capacity);
			const maxCapacity = Math.max(...capacityValues);
			const minCapacity = Math.min(...capacityValues);
			setMaxCapacity(maxCapacity);
			setMinCapacity(minCapacity);
		} catch (error) {
			console.error(error);
		}
	};

	useEffect(() => { //odešle pžadavek na spojení se serverem  a díky tomu se vypíší data do popupu
		getInfoCapacity()
	}, [])

	/*const handleCapacityChange = async (event) => {
		const selectedCapacity = event.target.value;
		const selectedItem = capacityData.find((item) => item.capacity === selectedCapacity);
		if (selectedItem) {
			setSelectedItemId(selectedItem.id);
			setCapacity(selectedCapacity);
		}
	};*/

	useEffect(() => {
		const selectedItem = capacityData.find((item) => item.capacity === capacity);
		if (selectedItem) {
		  setSelectedItemId(selectedItem.id);
		}
	  }, [capacityData, capacity]);





	return (
		<div>
			<label>Capacity</label>
			<input
				type="range"
				min={minCapacity}
				max={maxCapacity}
				value={capacity} //value prop je spojen s komponentní state pro zobrazení aktuální hodnoty
				step="1"
			onChange={(event) => { setCapacity(event.target.value)/*; handleCapacityChange(event)*/}} //onChange obsluhuje změny hodnoty vstupu a aktualizuje stav komponenty
			/>
			<button onClick={() => setCapacity(capacity + 1)}>+</button>
			<button onClick={() => setCapacity(capacity - 1)}>-</button>
			<p>{selectedItemId}</p>

			<p>{capacity}</p>

		</div>
	)
}

export default Capacity
