/*
Tento kód vypíše všechny markery z databáze a a údaje markeru (po kliknutí na marker)
*/

import "./Markers.css"
import React, { useState, useEffect } from 'react';
import { Marker, Popup } from 'react-leaflet'
import Axios from "axios"

function Markers() {
	const [list, setList] = useState([]);
	const [name, setName] = useState(0);


	useEffect(() => {
		// získání dat z databáze a nastavení počátečního stavu pro názvy jmen markerů
		Axios.get("http://localhost:3001/kot").then((response) => {
			setList(response.data)
			setName(response.data.map(val => val.name))
		})
	}, [])

	const updateMarker = (id) => { //databázové spojení pro aktualizaci dat
		// najdeme index markeru v poli a aktualizujeme odpovídající prvek pole s názvy jmen
		const index = list.findIndex(val => val.id === id)
		Axios.put("http://localhost:3001/update", { name: name[id], id: id }).then((response) => {
			setList(
				list.map((val) => {
					return val.id === id ? { ...val, name: name[id] } : val;
				})
			)
		})
	}

	const deleteMarker = (id) => { //smazání záznamu z databáze -> spojení s databází
		Axios.delete(`http://localhost:3001/delete/${id}`).then((response) => {
			setList(
				list.filter((val) => {
					return val.id != id;
				})
			)
		})

	}

	const handleNameChange = (index, name) => {
		// aktualizace názvu jmen markeru v poli
		const newName = [...name]
		newName[index] = name
		setName(newName)
	}

	return (
		<div>
			{list.map((val) => ( // získáme data z databáze, které vypíšeme do marker->popup

				<Marker key={val.id} position={[val.latitude, val.longitude]}>
					<Popup>
						<h1>{val.name} </h1>
						<input type="text" value={name[index]} onChange={(event) => handleNameChange(index, event.target.value)} />						<button onClick={() => {
							updateMarker(val.id)
						}
						}>Aktualizovat</button>
						<h2>{val.latitude}</h2>
						<h2>{val.longitude}</h2>

						<button onClick={() => deleteMarker(val.id)}>Smazat</button>
					</Popup>
				</Marker>
			))}
		</div>
	)
}

export default Markers
