/*
Tento kód vypíše všechny markery z databáze a a údaje markeru (po kliknutí na marker)
*/

import "./Markers.css"
import React, { useState, useEffect } from 'react';
import { Marker, Popup } from 'react-leaflet'
import Axios from "axios"

function Markers() {
	return (
		<div>
			{list.map((val) => ( // získáme data z databáze, které vypíšeme do marker->popup
				<Marker key={val.id} position={[val.latitude, val.longitude]}>
					<Popup>
						<h1>{val.name} </h1>
						<input type="text" onChange={(event) => setName(event.target.value)} />
						<button onClick={() => {
							updateMarker(val.id)
						}
						}>Aktualizovat</button>
						<h2>{val.latitude}</h2>
						<h2>{val.longitude}</h2>
						<h2>Capacity</h2>
						<p>{val.capacity}</p>
						<h2>Water deep</h2>
						<p>{val.waterDeep}</p>
						<h2>Wind</h2>
						{val.winds.map((wind) => (
							<p key={wind}>{wind}</p>
						))}
						<h2>Bottom</h2>
						{val.bottom.map((bottom) => (
							<p key={bottom}>{bottom}</p>
						))}

						<button onClick={() => deleteMarker(val.id)}>Smazat</button>
					</Popup>
				</Marker>
			))}
		</div>
	)
}

export default Markers
