import React, { useRef, useState } from 'react';
import { Popup } from 'react-leaflet';

function Button() {
	const [showPopup, setShowPopup] = useState(false);

	const CustomControl = ({ position }) => {
		const buttonRef = useRef(null);

		return (
			<div
				ref={buttonRef}
				style={{
					position: 'absolute',
					top: position[0],
					left: position[1],
					zIndex: 1000
				}}
			>
				<button onClick={() => setShowPopup(!showPopup)}>Tlačítko</button>
			</div>
		);
	};
	return (
		<div>
			<CustomControl position={[100, 100]}>
				{showPopup && (
					<Popup position={[100, 100]}>
						<h1>Ahoj</h1>
						{}
					</Popup>
				)}
			</CustomControl>

		</div>


	)
}

export default Button
