import React, { useState, useEffect } from 'react';
import Axios from "axios"




const WaterDeep = (props) => {

	const [waterDeep, setWaterDeep] = useState(10)
	const [maxWaterDeep, setMaxWaterDeep] = useState()
	const [minWaterDeep, setMinWaterDeep] = useState()
	const [selectedItemIdDeep, setSelectedItemIdDeep] = useState();
	const [deepData, setDeepData] = useState([]);



	const getInfoDeep = async () => {
		try {
			const response = await Axios.get("http://localhost:3001/deep");
			setDeepData(response.data);
			const waterDeepValues = response.data.map((item) => item.waterDeep);
			const maxWaterDeep = Math.max(...waterDeepValues);
			const minWaterDeep = Math.min(...waterDeepValues)
			setMaxWaterDeep(maxWaterDeep); // aktualizace stavu
			setMinWaterDeep(minWaterDeep)
			console.log

		} catch (error) {
			console.error(error);
		}
	}


	useEffect(() => { //odešle pžadavek na spojení se serverem  a díky tomu se vypíší data do popupu
		getInfoDeep()
	}, [])

	useEffect(() => {
		if (deepData.length > 0) {
			const selectedItem = deepData.find((item) => item.waterDeep === waterDeep);
			if (selectedItem) {
				setSelectedItemIdDeep(selectedItem.id);
				//props.onSelectedItemId(selectedItem.id)
			} else {
				setSelectedItemIdDeep(5);
				//props.onSelectedItemId(null)
			}
		}
	}, [deepData, waterDeep]);

	return (
		<div>
			<label>Water deep</label>
			<input
				type="range"
				min={minWaterDeep}
				max={maxWaterDeep}
				value={waterDeep} //value prop je spojen s komponentní state pro zobrazení aktuální hodnoty
				step="1"
				onChange={(event) => {
					const newCapacity = parseInt(event.target.value); // převést řetězec na číslo
					setWaterDeep(newCapacity); // aktualizovat stav capacity
					const selectedItem = deepData.find((item) => item.waterDeep === newCapacity); // najít odpovídající prvek v poli capacityData
					if (selectedItem) {
						setSelectedItemIdDeep(selectedItem.id); // aktualizovat stav selectedItemId
					} else {
						setSelectedItemIdDeep(15); // při neexistujícím prvku nastavit výchozí id
					}
				}} //onChange obsluhuje změny hodnoty vstupu a aktualizuje stav komponenty
			/>
			<p>{selectedItemIdDeep}</p>
			<p>{waterDeep}</p>
		</div>
	)
}

export default WaterDeep
