import './App.css';
import { MapContainer } from 'react-leaflet'
import { Control } from 'leaflet';
import Maps from './components/Maps';
import Markers from './components/Markers';

const App = () => {

  class MyButtonControl extends Control {
    createLeafletElement(props) {
      const button = document.createElement('button');
      button.innerHTML = 'Click me';
      button.style.backgroundColor = 'white';
      button.style.padding = '8px';
      button.style.borderRadius = '4px';
      button.style.border = '1px solid gray';
      button.onclick = props.onClick;
      return button;
    }

    componentDidMount() {
      const { position } = this.props;
      const leafletElement = this.createLeafletElement(this.props);
      this.leafletElement = leafletElement;
      this.props.leaflet.controlContainer.appendChild(leafletElement);
    }
  }

  return (
    <div>
      <MapContainer center={[44.119370, 15.231365]} zoom={13} scrollWheelZoom={true}>
        <Maps />
        <Markers />
        <MyButtonControl position="topleft" onClick={handleClick}/>
      </MapContainer>
    </div >

  )
}

export default App;
