import React, { useRef, useState } from 'react';
import "./Button.css"
import Axios from "axios"

function Button() {
	const [showInfo, setShowInfo] = useState(false);
	const [kotva, setKotva] = useState([])

	const CustomControl = ({ position }) => {
		const buttonRef = useRef(null);

		const getEmployee = () => {
			Axios.get("http://localhost:3001/kotva").then((response) => {
			  setEmployList(response.data)
			})
		  }

		const info = () => {
			return (
				<div className='info'>
					<label>Název</label>
					<input type="text" />

				</div>
			)
		}


		return (
			<div
				ref={buttonRef}
				style={{
					position: 'absolute',
					top: position[0],
					left: position[1],
					zIndex: 1000
				}}
			>
				<button onClick={() => setShowInfo(!showInfo)}>Tlačítko</button>
				{showInfo && info()}
			</div>
		);
	};


	return (
		<div>
			<CustomControl position={[300, 100]}>
			</CustomControl>

		</div>


	)
}

export default Button
