import React, { useState, useEffect } from 'react';
import { Marker, Popup } from 'react-leaflet'
import Axios from "axios"


const UpdateAnchorage = () => {

	const [list, setList] = useState([]);
	const [name, setName] = useState(0);

	const updateMarker = (id, latitude, longitude) => { //databázové spojení pro aktualizaci dat
		Axios.put("http://localhost:3001/update", { name: name, id: id, latitude: latitude, longitude: longitude }).then((response) => {
			setList(
				list.map((val) => {
					return val.id == id
						? {
							id: val.id,
							name: name,
							latitude: val.latitude,
							longitude: val.longitude
						}
						: val;
				})
			)
		})
	}

	return (
		<div>
			<input type="text" value={val.name} onChange={(event) => setName(event.target.value)} />
			<button onClick={() => {
				updateMarker(val.id)
			}
			}>Aktualizovat</button>

		</div>
	)

}

export default UpdateAnchorage
