import React, { useRef, useState } from 'react';
import "./Button.css"
import Axios from "axios"

function Button() {
	const [showInfo, setShowInfo] = useState(false);
	const [kotva, setKotva] = useState([])
	const [name, setName] = useState("");
	const [list, setList] = useState([]);


	const CustomControl = ({ position }) => {
		const buttonRef = useRef(null);

		const getInfo = () => {
			Axios.get("http://localhost:3001/kotva").then((response) => {
				setList(response.data)
			})
		}
		const ResInfo = () => {
			return (
				<div>
					{list.map((val, key) => {
						return (
							<div key={key}>
								<h1>{val.name}</h1>
							</div>
						);
					})}
				</div>
			);
		}

		const addInfo = () => {
			Axios.post("http://localhost:3001/create", {
			  name: name,
			}).then(() => console.log("úspěch"))
		  }


		const info = () => {
			return (
				<div className='info'>
					<label>Název</label>
					<input type="text" onChange={(event) => setName(event.target.value)} />
					<button onClick={addInfo}>Přidat info</button>
					<ResInfo />
				</div>
			)
		}



		return (
			<div
				ref={buttonRef}
				style={{
					position: 'absolute',
					top: position[0],
					left: position[1],
					zIndex: 1000
				}}
			>
				<button onClick={() => setShowInfo(!showInfo)}>Tlačítko</button>
				{showInfo && info()}
			</div>
		);
	};


	return (
		<div>
			<CustomControl position={[300, 100]}>
			</CustomControl>

		</div>


	)
}

export default Button
